<?phpfunction th1_load_css() {    if (!is_admin()){		        // CSS Plugins        wp_enqueue_style('bootstrap', TH1_PLUGINS . 'bootstrap/css/bootstrap.min.css');        wp_enqueue_style('font-awesome', TH1_PLUGINS . 'font-awesome/css/font-awesome.min.css');		        // CUBE link where is used (onepager)		if (is_page_template('template-onepager.php')) {			wp_enqueue_style('cube', TH1_PLUGINS . 'cubeportfolio/css/cubeportfolio.min.css');			wp_enqueue_style('magnific-popup', TH1_PLUGINS . 'magnific/magnific-popup.css');		}				wp_enqueue_style('owl-carousel', TH1_PLUGINS . 'owl-carousel/owl.carousel.css');		wp_enqueue_style('owl-theme', TH1_PLUGINS . 'owl-carousel/owl.theme.css');		        // Load our main stylesheet.        wp_enqueue_style( 'main-css', get_stylesheet_uri() );		        // Custom colors        $th_select_stylesheet=th_theme_data('th_select_stylesheet',''); // Stylesheet select        if ( $th_select_stylesheet != '' && $th_select_stylesheet !== 'default.css' && $th_select_stylesheet !== 'custom'){            wp_enqueue_style('main-color-theme', TH1_CSS . 'colors/'.$th_select_stylesheet);        }		wp_enqueue_style('dynamic', TH1_CSS . 'dynamic.php');		        // Google Web Fonts        wp_enqueue_style('raleway', 'http://fonts.googleapis.com/css?family=Raleway:400,100,200,300,500,600,700');        wp_register_style('elegant', TH1_PLUGINS . 'elegant/style.css');		        // RTL Layout        if(th_theme_data('enable_rtl_layout') == 1){            wp_enqueue_style('rtl-css', TH1_CSS . 'rtl.css');            wp_enqueue_style('bootstrap-rtl', '//cdn.rawgit.com/morteza/bootstrap-rtl/master/dist/cdnjs/3.3.1/css/bootstrap-rtl.min.css');        }    }}add_action('wp_enqueue_scripts', 'th1_load_css');// Dashboard stylesfunction th_admin_styles() {    wp_enqueue_style('th-admin', get_template_directory_uri() . '/framework/theme-options/assets/css/th-admin.css');//    wp_register_style('font-awesome', TH1_PLUGINS . 'font-awesome/css/font-awesome.min.css');////    wp_enqueue_style('font-awesome');    wp_enqueue_style('th-admin');}add_action( 'admin_enqueue_scripts',  'th_admin_styles' );?>