<?phpfunction th1_load_js() {    if (!is_admin()){        // Javascript Plugins        wp_enqueue_script( 'jquery' );        wp_enqueue_script('moderniz',TH1_PLUGINS. 'modernizr-2.8.3.js', array('jquery'),'', true);        wp_enqueue_script('easing',TH1_PLUGINS. 'jquery.easing.min.js', array('jquery'),'', true);        wp_enqueue_script('scrollReveal',TH1_PLUGINS. 'scrollReveal.min.js', array('jquery'),'', true);		wp_enqueue_script('magnific-popup',TH1_PLUGINS. 'magnific/jquery.magnific-popup.min.js', array('jquery'),'', true);		        //wp_enqueue_script('typed',TH1_PLUGINS. 'typed.min.js', array('jquery'),'', true);        wp_enqueue_script('simpleparallax',TH1_PLUGINS. 'jquery.simpleparallax.min.js', array('jquery'),'', true);        wp_enqueue_script('bootstrap_js',TH1_PLUGINS. 'bootstrap/js/bootstrap.min.js', array('jquery'),'', true);		wp_enqueue_script('owl-carousel',TH1_PLUGINS. 'owl-carousel/owl.carousel.min.js', array('jquery'),'', true);		wp_enqueue_script('owl',TH1_JS. 'owl.js', array('jquery'),'', true);				if ( is_singular() ) wp_enqueue_script( "comment-reply" );		wp_register_script('main',TH1_JS. 'main.js', array('jquery'),'', true);				if ( is_page() || is_front_page() || is_home() ) {			wp_enqueue_script('owl-carousel',TH1_PLUGINS. 'owl-carousel/owl.carousel.min.js', array('jquery'),'', true);            wp_enqueue_script('owl',TH1_JS. 'owl.js', array('jquery'),'', true);			if (is_page_template('template-onepager.php')) {				wp_enqueue_script('cubeportfolio',TH1_PLUGINS. 'cubeportfolio/js/jquery.cubeportfolio.min.js', array('jquery'),'', true);				wp_enqueue_script('cubes',TH1_JS. 'cube.js', array('jquery'),'', true); // Portfolio				wp_enqueue_script('testimonials',TH1_JS. 'testimonials.js', array('jquery'),'', true);				wp_enqueue_script('typed',TH1_PLUGINS. 'typed.min.js', array('jquery'),'', true);				wp_enqueue_script('numscroller',TH1_PLUGINS. 'numscroller-1.0.js', array('jquery'),'', true);				wp_register_script('gmap','https://maps.googleapis.com/maps/api/js?v=3.exp', array('jquery'),'', true);			}		}		        if(is_singular( 'portfolio' )){            wp_enqueue_script('numscroller',TH1_PLUGINS. 'numscroller-1.0.js', array('jquery'),'', true);            wp_enqueue_script('owl-carousel',TH1_PLUGINS. 'owl-carousel/owl.carousel.min.js', array('jquery'),'', true);            wp_enqueue_script('owl',TH1_JS. 'owl.js', array('jquery'),'', true);        }			        if( is_single() ){            wp_enqueue_script('postshare',TH1_JS. 'postshare.js', array('jquery'),'', true);        }		wp_enqueue_script('main', '', '', '', true);    }}add_action('wp_enqueue_scripts', 'th1_load_js');// Dashboard scriptsfunction th_admin_scripts() {    wp_register_script('script1',get_template_directory_uri() . '/framework/theme-options/assets/js/scripts.js', '', '', true);    wp_enqueue_script('script1', '', '', '', true);}add_action( 'admin_enqueue_scripts', 'th_admin_scripts' );?>