<?php$sidebar_position=th_theme_data('home_sidebar_position','right');$content_class = "col-sm-12";if($sidebar_position == "no"){    $page_class="col-lg-12 col-md-12"; $content_class = '';}else{    $page_class="col-sm-9 blog-left-side";}if($sidebar_position == "left"){    $page_class .=' pull-right';}?><?php get_header(); ?>    <!-- Blog Full Width Header -->    <div class="jumbotron blog-fullwidth-header">        <div class="container">            <div class="row">                <!-- Title and Description -->                <div class="elements-title text-center">                    <h1><?php printf( __( 'Search Results for: %s','rook' ), get_search_query() ); ?></h1>                </div>            </div> <!-- /.row -->        </div> <!-- /.container -->    </div>    <!-- End Blog Full Width Header -->    <!-- Blog Full Width Container -->    <div class="container blog-fullwidth-container">        <div class="<?php echo esc_attr($page_class); ?>"><?php            if ( have_posts() ) :                while ( have_posts() ) : the_post(); ?>                    <div <?php post_class(esc_attr($content_class)) ?>>                        <div class="blog-item"><?php                            get_template_part( 'format', get_post_format() ); ?>                        </div>                    </div> <?php                endwhile;            else : echo esc_html_e('No posts found', 'rook' );            endif;?>        </div>        <?php if($sidebar_position!="no"){ ?>            <!-- Blog Sidebar -->            <div id="sidebar" class="col-sm-3">                <?php get_sidebar(); ?>            </div>            <!-- End Blog Sidebar-->        <?php } ?>        <!-- Blog Pagination -->        <div class="blog-pagination">            <div class="col-sm-12">                <?php th_pagination(); ?>            </div>        </div>    </div> <!-- /.container -->    <!-- End Blog Full Width Container --><?php get_footer(); ?>